import Quote from "./quote";
import axios from "axios";
import TwitterIcon from '@material-ui/icons/Twitter';

function App(){

    const [datarray,setdataarray]=useState([]);

    useEffect(()=>{
        axios.get("https://gist.githubusercontent.com/camperbot/5a022b72e96c4c9585c32bf6a75f62d9/raw/e3c6895ce42069f0ee7e991229064f167fe8ccdc/quotes.json")
        .then(res=>{
            console.log(res);
            setdataarray(res.data);
        })
        .catch(err=>{
            console.log(err)
        });
    },[]);

return(
<div className="quote-box">
    <Quote text="nnnnnnnnnnnnnnn" author="hjjjjjjk"/>
    <button className="button tweet" title="Tweet this quote"><TwitterIcon/></button>
    <button className="button tumbler" title="Share on Tumbler">t</button>
    <button className="button newquote">New Quote</button>
</div>
);
}
export default App;
