import React,{useEffect,useState} from "react";
import Quote from "./quote";






function App(){

    const [datarray,setdataarray]=useState([]);
    const [srandind,setranind]=useState(null);
    useEffect(async () => {
        const data = await fetch('https://gist.githubusercontent.com/natebass/b0a548425a73bdf8ea5c618149fe1fce/raw/f4231cd5961f026264bb6bb3a6c41671b044f1f4/quotes.json');
        const quotes = await data.json();
        setdataarray(quotes);
        setranind((Math.floor(Math.random()))*quotes.length-1);
      }, []);

      function quoteselection(){
        if (!datarray.length || !Number.isInteger(srandind)) {
            return undefined;
          }
          return datarray[srandind];
      }

      function indexfunc(){
          setranind(generatequoteindex());
      }


      function generatequoteindex()
      {
        if (!datarray.length) {
            return undefined;
          }
          return (Math.floor(Math.random()))*datarray.length-1;
      }


return(
<div className="quote-box">
    <Quote selectedquote= {quoteselection}
    selectrandindex={indexfunc}/>:nill
</div>
);
}
export default App;
